import React, { Component } from "react";
import PostDataService from "../../../services/posts.js";

export default class Post extends Component {
  state = {
    title: "",
    text: "",
  };

  componentDidMount() {
    this.getPosts();
  }

  getPosts = () => {
    PostDataService.get(this.props.id).then((response) => {
      this.setState({ title: response.title, text: response.text });
    });
  };

  render() {
    return <p>{this.state.title}</p>;
  }
}
