import React, { Component } from "react";
import PostDataService from "../../../services/posts.js";
import ReactEmbedGist from "react-embed-gist";

export default class Post extends Component {
  state = {
    text: "",
  };

  componentDidMount() {
    this.getPosts();
  }

  getPosts = () => {
    PostDataService.get(this.props.title).then((response) => {
      this.setState({ text: response.data.posts[0].text });
    });
  };

  render() {
    return (
      <div>
        <p>{this.props.title}</p>
        <p>{this.state.text}</p>
        <ReactEmbedGist gist="YeluguriMonish/3247ef2ecf7cfe4130ca44214fdf9a08" />
      </div>
    );
  }
}
